import React, { useState, useRef } from 'react';
import { Button, Typography, Card, CardContent } from '@mui/material';

const AudioRecorder = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [note, setNote] = useState('');
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const handleStartRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;
    audioChunksRef.current = [];

    mediaRecorder.ondataavailable = event => {
      if (event.data.size > 0) {
        audioChunksRef.current.push(event.data);
      }
    };

    mediaRecorder.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });

      // ⬇️ Send to backend
      const formData = new FormData();
      formData.append('file', audioBlob, 'recording.webm');

      try {
        const response = await fetch('http://localhost:5000/api/detect-note', {
          method: 'POST',
          body: formData
        });

        const data = await response.json();
        setNote(data.note || 'No note detected');
      } catch (err) {
        console.error('Error sending audio:', err);
        setNote('Error');
      }

      // ⬇️ Trigger download
      const url = URL.createObjectURL(audioBlob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'input_audio.webm';
      a.click();
      URL.revokeObjectURL(url);
    };

    mediaRecorder.start();
    setIsRecording(true);

    setTimeout(() => {
      mediaRecorder.stop();
      setIsRecording(false);
    }, 3000); // Stop after 3 seconds
  };

  return (
    <Card sx={{ maxWidth: 400, margin: 'auto', mt: 5, p: 2, borderRadius: 4, boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h5" gutterBottom>
          Guitar Note Detector
        </Typography>
        <Button 
          variant="contained" 
          color={isRecording ? 'secondary' : 'primary'} 
          onClick={handleStartRecording}
          disabled={isRecording}
          fullWidth
        >
          {isRecording ? 'Recording...' : 'Start Recording'}
        </Button>
        <Typography variant="h6" mt={3} textAlign="center">
          {note && `Detected Note: ${note}`}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default AudioRecorder;
