import React from 'react';
import './TunerNeedle.css';

const TunerNeedle = ({ frequency, targetFrequency }) => {
  if (!frequency || !targetFrequency) return null;

  const deviation = frequency - targetFrequency;
  const maxDeviation = 10; // Hz ± range
  const angle = Math.max(-45, Math.min(45, (deviation / maxDeviation) * 45)); // Clamp angle to ±45°

  return (
    <div className="tuner-container">
      <div className="tuner-base" />
      <div className="needle" style={{ transform: `rotate(${angle}deg)` }} />
    </div>
  );
};

export default TunerNeedle;
